sourcesContent:
import express from 'express';
import multer from 'multer';
const path = require('path');

const bodyParser = require('body-parser');
const bcrypt = require('bcryptjs');
const jwt = require('jsonwebtoken');

if (process.env.NODE_ENV !== 'production') {
  require('dotenv').config()
}
const cors = require('cors');
const flash = require('express-flash')
const methodOverride = require('method-override');

const { MongoClient, ServerApiVersion, ObjectId } = require('mongodb');

const storage = multer.diskStorage({
  destination: function (req, file, cb) {
    cb(null, './assets'); // Specify the directory where uploaded files will be stored
  },
  filename: function (req, file, cb) {
    cb(null, Date.now() + path.extname(file.originalname)); // Rename the file to avoid conflicts
  }
});

const uri = "mongodb+srv://erikpyaslane:tfxzxxkd8u@portfolio.4dktmad.mongodb.net/?retryWrites=true&w=majority"
const client = new MongoClient(uri, {
  serverApi: {
    version: ServerApiVersion.v1,
    strict: true,
    deprecationErrors: true,
  }
});

/*************************************
 *
 *      DATABASE
 *  
 ************************************/


async function connectToDatabase() {
  try {
    await client.connect();
    console.log("Connected to MongoDB");
  } catch (error) {
    console.error("Error connecting to MongoDB:", error);
    throw error;
  }
}

async function closeDatabaseConnection() {
  try {
    await client.close();
    console.log("Closed MongoDB connection");
  } catch (error) {
    console.error("Error closing MongoDB connection:", error);
    throw error;
  }
}

// Connect to MongoDB when the application starts
connectToDatabase().catch(console.dir);

const app = express();
app.use(express.json());
app.use(flash());
app.use(bodyParser.json());
app.use(cors());

app.use(methodOverride('_method'))

app.use('/images', express.static(path.join(__dirname, "../assets")))
app.use(express.static(path.resolve(__dirname, "../dist"),
  { maxAge: '1y', etag: false },));


/*************************************
 *
 *      AUTHENTICATION
 *
 ************************************/

app.post('/api/login', async (req, res) => {
  const { username, password } = req.body;
  console.log(username)
  console.log(password)
  try {
    const db = client.db('Portfolio');
    const users = db.collection('Admins');
    const user = await users.findOne({ username });
    console.log(user)
    if (!user || !bcrypt.compareSync(password, user.password)) {
      return res.status(401).json({ message: 'Invalid credentials' });
    }
    const token = jwt.sign({ userId: user._id }, 'secretkey', { expiresIn: '1h' });
    console.log(token)
    res.json({ token });
  } catch (error) {
    console.error('Login error:', error);
    res.status(500).json({ message: 'Internal server error' });
  }
});


/*************************************
 *
 *      PROJECTS 
 *  
 ************************************/

app.get('/api/projects', async (req, res) => {
  try {
    const db = client.db("Portfolio");
    const projects = await db.collection("Projects").find({}).toArray();
